    {/* <p>Welcome {isLoggedIn &&'Phanidharsai'}</p> 
     if we use above it prints "Welcome phanidharsai" if isLoggedIn is true else "Welcome" */}
    <p>Welcome {isLoggedIn}</p>
    <button onClick={()=>finalStatus(true)}>Log in</button>
    
    </div>)
}
